#include<cstdio>
#include<cmath>
#include<vector>
#include<string>
#include<algorithm>
#include<functional>

using namespace std;

#define MOD 1000000007

int safe(int x, int y, int n)
{
	return x >= 0 && y >= 0 && x < n&&y < n;
}

class Solution {
public:
	vector<int> pathsWithMaxScore(vector<string>& board) {
		int n;
		int dx[3] = { 1,0,1 };
		int dy[3] = { 0,1,1 };
		pair<int, int> dp[101][101];
		vector<int> rvalue;

		n = board.size();

		for (int i = 0; i < n; i++) {
			for (int j = 0; j < n; j++) {
				dp[i][j] = { 0,0 };
			}
		}

		dp[n - 1][n - 1] = { 0,1 };

		for (int i = n - 1; i >= 0; i--) {
			for (int j = n - 1 - (i == n - 1); j >= 0; j--) {
				pair<int, int> curmax = { 0,0 };

				if (board[i][j] == 'X')
				{
					dp[i][j] = { 0,0 };

					continue;
				}

				for (int k = 0; k < 3; k++) {
					int nx, ny;
					nx = i + dx[k];
					ny = j + dy[k];

					if (safe(nx, ny, n)) {
						if (dp[nx][ny].first > curmax.first)
						{
							curmax = dp[nx][ny];
						}

						else if (dp[nx][ny].first == curmax.first)
						{
							curmax.second = (curmax.second + dp[nx][ny].second) % MOD;
						}
					}
				}

				dp[i][j] = curmax;

				if (dp[i][j].second != 0)
				{
					dp[i][j].first += board[i][j] - '0';
				}
			}
		}

		if (dp[0][0].second == 0)
		{
			rvalue.push_back(0);
			rvalue.push_back(0);

			return rvalue;
		}

		dp[0][0].first -= ('E' - '0');

		rvalue.push_back(dp[0][0].first);
		rvalue.push_back(dp[0][0].second);

		return rvalue;
	}
};

